package com.Myexample.MyWebApp.Service;import com.Myexample.MyWebApp.Entity.students;import com.Myexample.MyWebApp.Repository.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.ArrayList;import java.util.List;import java.util.Optional;@Servicepublic class UserService {    @Autowired    private UserRepository userRepository;    public List<students> ListAllUsers()    {        Iterable<students> iterableStudents = userRepository.findAll();        List<students> userList = new ArrayList<>();        iterableStudents.forEach(userList::add);        return userList;    }    public void save(students student)    {        userRepository.save(student);    }    public students get(Integer id) throws UserNotFoundException {        Optional<students> result = userRepository.findById(id);        if(result.isPresent())        {            return result.get();        }        throw new UserNotFoundException("Could not find any users with ID", + id);    }    public void delete(Integer id) throws UserNotFoundException {        Long count = userRepository.countById(id);        if(count == null || count == 0)        {            throw new UserNotFoundException("Could not find any users with ID", + id);        }        userRepository.deleteById(id);    }}